import React from "react";

export const authInitialState = {
  name: '',
  email: '',
  password: '',
  passwordConfirmation: '',
  rememberMe: '1',
  openSignup: false,
  openLogin: false,
  openPasswordReset: false,
  loginUser: {},
  loggedIn: false,
}

export const authReducer = (state, action) => {
  switch (action.type) {
    case 'name':
      return { neme: action.payload }
    case 'email':
      return { email: action.payload }
    case 'password':
      return { password: action.payload }
    case 'passwordConfirmation':
      return { passwordConfirmation: action.payload }
    case 'rememberMe':
      return { rememberMe: action.payload }
    case 'openSignup':
      return { openSignup: true }
    case 'openLogin':
      return { openLogin: true }
    case 'openPasswordReset':
      return { openLogin: false, openPasswordReset: true }
    case 'login':
      return {
        loginUser: action.payload,
        loggedIn: true,
      }
    case 'logout':
      return {
        loginUser: {},
        loggedIn: false,
      }
    case 'close':
      return {
        openSignup: false,
        openLogin: false,
        openPasswordReset: false,
      }
    default:
      return authInitialState
  }
}
