import React, { useState } from 'react';
import { BrowserRouter, Switch, Route } from 'react-router-dom'
import './App.css';
// コンテイナー
import { Home } from './containers/Home'
import { User } from './containers/User'
import { Users } from './containers/Users'
import { Header } from "../components/Header"


export default function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState({});

  // ログインのコールバック関数
  const handleLogIn = (user) => {
    setIsLoggedIn(true);
    setUser(user);
  }

  // ログアウトのコールバック関数
  const handleLogOut = () => {
    setIsLoggedIn(false);
    setUser({});
  }

  const [openSignUpDialog, setOpenSignUpDialog] = useState(false)
  const [openLogInDialog, setOpenLogInDialog] = useState(false)
  // Dialogを開く関数(新規登録、ログイン)
  const handleOpenSignUp = () => setOpenSignUpDialog(true)
  const handleOpenLogIn = () => setOpenLogInDialog(true)
  // Dialogを閉じる関数（新規登録、ログイン）
  const handleCloseSignUp = () => setOpenSignUpDialog(false)
  const handleCloseLogIn = () => setOpenLogInDialog(false)

  return (
    <BrowserRouter>
      <Header
        open={openLogInDialog}
        handleClickOpenLogIn={handleOpenLogIn}
        handleClose={handleCloseLogIn}
        isLoggedIn={isLoggedIn}
        handleLogIn={handleLogIn}
        handleLogOut={handleLogOut}
        user={props.user}
      />
      <Switch>
        <Route exact
          path="/">
          <Home
            isLoggedIn={isLoggedIn}
            handleLogIn={handleLogIn}
            handleLogOut={handleLogOut}
          />
        </Route>
      </Switch>
      <Switch>
        <Route exact
          path="/user/:id"
          render={({ match }) =>
            <User
              isLoggedIn={isLoggedIn}
              handleLogIn={handleLogIn}
              handleLogOut={handleLogOut}
              match={match}
              user={user}
            />
          }>
        </Route>
      </Switch>
      <Switch>
        <Route exact
          path="/users">
          <Users
            isLoggedIn={isLoggedIn}
            handleLogIn={handleLogIn}
            handleLogOut={handleLogOut}
            user={user}
          />
        </Route>
      </Switch>
    </BrowserRouter>
  );
}
