import React, { useEffect, useState } from "react";
// styles
import { IconButton } from "@mui/material";
// アイコン
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import FavoriteIcon from '@mui/icons-material/Favorite';
import { red } from "@mui/material/colors";
// api
import { postLike, postUnlike } from "../../apis/likes";
// import { fetchLikedMicropost } from "../../apis/microposts";

export const LikeButton = (props) => {
  const [like, setLike] = useState(props.likedStatus)
  const handleLike = () => {
    postLike({
      userId: props.loginUserId,
      micropostId: props.micropostId,
    })
      .then(setLike(true)
      )
  }

  const handleUnlike = () => {
    postUnlike({
      userId: props.loginUserId,
      micropostId: props.micropostId,
    })
      .then(setLike(false)
      )
  }

  // useEffect(() => {
  //   fetchLikedMicropost(props.micropostId)
  //     .then(data => {
  //       setLike(data.liked_micropost)
  //     })
  // })

  return (
    <>
      {like === true
        ?
        <IconButton onClick={handleUnlike} >
          <FavoriteIcon sx={{ color: red[500] }} />
        </IconButton>
        :
        <IconButton onClick={handleLike} >
          <FavoriteBorderIcon color="primary" />
        </IconButton>
      }
    </>
  )
}
