import React, { useState } from "react"
import { useHistory } from "react-router-dom";
// Style
import Avatar from "@mui/material/Avatar";
import Typography from "@mui/material/Typography";
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import { makeStyles } from '@material-ui/styles';
import { theme } from '../../styled/theme'
// Icon
import AccountCircle from "@mui/icons-material/AccountCircle";
// Component
import { SettingDialog } from "../Dialogs/SettingDialog";
import { FollowButton } from "../Buttons/FollowButton";

const useStyles = makeStyles(theme => ({
  button: {
    background: '#0059b2',
    border: 0,
    borderRadius: 3,
    color: 'white',
    height: 30,
    padding: '15px 20px',
  },
}));

export const UserInfo = ({
  loginUser,
  profile,
}) => {
  // 設定ダイアログの開閉
  const history = useHistory();
  const classes = useStyles();
  const [open, setOpen] = useState(false);

  return (
    <>
      <Box sx={{
        alignItems: 'center',
        display: 'flex',
        pb: 5,
      }}>
        <Avatar sx={{
          alignItems: 'center',
          width: 100, height: 100,
          p: 4,
        }} >
          <AccountCircle sx={{ fontSize: 100 }} />
        </Avatar>
        <Box sx={{ pl: 3 }}>
          <Typography variant="h4">
            <Box sx={{ letterSpacing: 6 }}>{profile.user.name}</Box>
          </Typography>
          <Typography variant="body1">
            <Box sx={{ letterSpacing: 3, py: 2 }}>{profile.user.profile}</Box>
          </Typography>
          <Box sx={{ display: 'flex' }}>
            <Typography variant="body1">
              <Box
                onClick={() => history.push(`/users/${profile.user.id}/following`)}
                sx={{ letterSpacing: 2, pr: 4, cursor: 'pointer' }}
              >
                <b>{profile.followingIds.length}</b> フォロー中
              </Box>
            </Typography>
            <Typography
              variant="body1"
              sx={{ flexGrow: 1 }}
            >
              <Box
                onClick={() => history.push(`/users/${profile.user.id}/followers`)}
                sx={{ letterSpacing: 2, cursor: 'pointer' }}
              >
                <b>{profile.followersIds.length}</b> フォロワー
              </Box>
            </Typography>
            {
              (loginUser.id === profile.user.id) ? (
                <Button
                  className={classes.button}
                  onClick={() => { setOpen(true) }}
                >
                  プロフィール編集
                </Button>
              ) : (
                <FollowButton
                  userId={profile.user.id}
                  followStatus={profile.followStatus}
                />
              )
            }
          </Box>
          <Typography variant="h6">
            <Box sx={{ letterSpacing: 4, pt: 2 }}>★ 目標睡眠時間：{profile.user.ideal_sleeping_hours} 時間</Box>
          </Typography>
        </Box>
      </Box>

      <SettingDialog
        handleClose={() => { setOpen(false) }}
        open={open}
        dataUserFetch={profile.dataUserFetch}
      />
    </>
  )
}
