import React from "react";
// Style
import Box from '@mui/material/Box';
import Button from "@mui/material/Button";
import { createStyles, makeStyles } from "@material-ui/core/styles";
// Api
import { postBook, updateBook } from "../../apis/books";

const useStyles = makeStyles(() =>
  createStyles({
    'recommend': {
      background: '#1565c0',
      border: 0,
      borderRadius: 4,
      color: 'white',
      height: 30,
      padding: '15px 20px',
      margin: '20px 20px'
    },
  }),
);

export const RecommendButton = ({
  book,
  registration,
  subscribed,
}) => {
  const classes = useStyles();
  const handleClick = (boolean) => {
    { // ユーザー未登録本の場合、CreateでDBに登録する
      !subscribed && (
        postBook({
          read: boolean,
          registration: registration,
          book: book,
        })
          .then(data => {
            // ぺーじ遷移を加える
            data.message &&
              alert(data.message)
          })
      )
    }
  }

  return (
    <Box>
      <Button
        className={classes.recommend}
        color="primary"
        onClick={() => handleClick("false")}
        variant="outlined"
      >
        私のおすすめにする
      </Button>
    </Box>
  )
}
