import React from "react";
// styles
import { Button } from "@mui/material";
// api
import { postbook } from "../../apis/books";

export const ReadButton = (props) => {
  const handleClick = (boolean) => {
    postbook({
      bookIsbn: props.bookIsbn,
      read: boolean,
    })
      .then(data => {
        data.subucription.read
          ? alert('読んだ本に登録しました')
          : alert('積んでいる本に登録しました')
      })
  }

  return (
    <>
      <Button
        color="primary"
        onClick={handleClick(false)}
        size="small"
      >
        積む
      </Button>
      <Button
        color="primary"
        onClick={handleClick(true)}
        size="small"
      >
        読了
      </Button>
    </>
  )
}
