import React from "react"
// Style
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';

export const SleepInfo = ({
  userName,
  sleepDebt,
  sleepSaving,
}) => {

  return (
    <Box sx={{
      alignItems: "center",
      justifyContent: 'center',
    }}>
      {
        sleepDebt &&
        <Box>
          <Typography variant="h4" component="div" >
            <Box sx={{ letterSpacing: 6, my: 2 }}>{userName}さんの睡眠負債は</Box>
          </Typography>
          <Typography variant="h1" component="div" sx={{ textAlign: 'center', }} >
            <Box sx={{ letterSpacing: 6, my: 2 }}>{sleepDebt}時間</Box>
          </Typography>
          <Typography variant="h5" component="div" >
            <Box sx={{ letterSpacing: 4, my: 6 }}>さあ、ひと眠りか、ふた眠りしよう...</Box>
          </Typography>
        </Box>
      }
      {
        sleepSaving &&
        <Box>
          <h3>{userName}さんは目標より</h3>
          <h1>{sleepSaving} 時間</h1>
          <h3>睡眠をとっています</h3>
          <h3>この調子で睡眠時間を確保していきましょう</h3>
        </Box>
      }
      {
        !sleepDebt && !sleepSaving &&
        <Typography variant="h2" component="div" >
          <Box sx={{ letterSpacing: 6 }}>さあ、睡眠を記録しよう</Box>
        </Typography>
      }
    </Box>
  )
}
