import React, { useEffect, useState } from 'react';
// Style
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
// Api
//Component
import { Date } from '../Forms/Date';
import { SleepingHours } from '../Forms/SleepingHours'
import { Feeling } from '../Forms/Feeling'

const formatDate = (date) => {
  return (
    date.getFullYear() + '年' +
    (date.getMonth() + 1) + '月' +
    date.getDate() + '日'
  )
}

export const DiaryDialog = ({
  handleClose,
  open,
  recordDispatch,
  recordState,
}) => {


  // 更新情報を送信する
  const submitUpdate = () => {
    // patchDiary(daiary: {
    //   id: ,
    //   sleepingHours: ,
    //   feeling: ,
    // }).then(() => {
    //   handleClose();
    // }):
  }


  // 新規登録ダイアログの内容を返す
  return (
    <Dialog
      open={open}
      onClose={handleClose}
    >
      <DialogTitle>
        {formatDate(recordState.date)}の睡眠日記
      </DialogTitle>

      <DialogContent>
        <Date
          date={recordState.date}
          handleChange={e => recordDispatch({
            type: 'date',
            payload: e.target.value
          })}
        />
        <SleepingHours
          sleepingHours={recordState.sleepingHours}
          handleChange={e => recordDispatch({
            type: 'sleepingHours',
            payload: e.target.value
          })}
        />
        <Feeling
          feeling={recordState.feeling}
          recordDispatch={recordDispatch}
        />
      </DialogContent>

      <DialogActions>
        <Button onClick={submitUpdate} type='submit'>
          編集する
        </Button>
      </DialogActions>
    </Dialog>
  );
}
