import React from "react";
// コンポーネント
import { BookCard } from "./BookCard";

export const BookCards = ({ readBooks, dataFatching }) => {
  return (
    <>
      {readBooks.length === 0 ? (
        <h2>読了登録した本はありません。</h2>
      ) : (
        readBooks.map(book =>
          <Grid item key={book.id}
            xs={6} sm={4} sx={{ p: 2, bgcolor: 'grey.100' }}>
            <BookCard
              book={book}
              dataFatching={dataFatching}
            />
          </Grid>
        )
      )
      }
    </>
  )
}
