import React, { useState, useEffect } from "react";
// styles
import { createStyles, makeStyles } from "@material-ui/core/styles";
import List from "@mui/material/List";
// api
import { fetchMessages } from "../../apis/users"
// コンテイナー
import { Message } from "../../components/Messages/Message";

const useStyles = makeStyles(() =>
  createStyles({
    "messages": {
      height: "400px",
      padding: "0",
      overflow: "auto"
    }
  }),
);

export const MessageRoom = (props) => {
  const classes = useStyles();
  const [messages, setMessages] = useState([])
  // メッセージルーム一覧を取得する
  useEffect(() => {
    fetchMessages({ userId: props.loginUser.id })
      .then(data => setMessages(data.messages))
    return () => setMessages([])
  }, [])

  return (
    <>
      <h3>{props.loginUser.name}</h3>
      <List className={classes.messages} id={"scroll-area"}>
        {
          messages.map((message, index) => {
            <Message
              text={message.content}
              key={index}
              loginUserId={props.loginUser.id}
            />
          })
        }
      </List>
    </>
  )
}
