import React, { Fragment, useEffect, useState } from "react";
import Button from '@mui/material/Button';

// api
import { fetchHome } from "../apis/home";
import { SignUpDialog } from "../components/SignUpDialog"
import { LogInDialog } from "../components/LogInDialog"

export const Home = (props) => {

  const [open, setOpen] = useState({
    signUpDialog: false,
    logInDialog: false,
  });

  const handleClickOpenSignUp = () => {
    setOpen({ signUpDialog: true });
  };

  const handleClickOpenLogIn = () => {
    setOpen({ logInDialog: true });
  };

  const handleClose = () => {
    setOpen(false);
  };

  useEffect(() => {
    fetchHome()
      .then((data) =>
        console.log(data)
      )
  }, [])

  return (
    <Fragment>
      <h1>HOME</h1>
      <h2>ログイン状態：{props.loggedInStatus}</h2>

      <Button variant="outlined" onClick={handleClickOpenSignUp}>
        新規登録
        <SignUpDialog open={open.signUpDialog} handleClose={handleClose} handleLogin={props.handleLogin} />
      </Button>

      <Button variant="outlined" onClick={handleClickOpenLogIn}>
        ログイン
        <LogInDialog open={open.logInDialog} handleClose={handleClose} handleLogin={props.handleLogin} />
      </Button>
    </Fragment>
  )
}
