import React, { useState, useReducer } from "react";
import axios from "axios";
// styles
import Button from "@mui/material/Button";
//api
import { postFollow } from "../../apis/relationships";
import { deleteUnfollow } from "../../apis/relationships";
import { fetchFollow } from "../../apis/relationships";
// Reducer
import { dataInitialState, dataReducer } from '../../reducer/DataReducer';


export const FollowButton = ({
  userId,
  followStatus,
}) => {
  const [following, setFollowing] = useState(followStatus)
  const [dataState, dataDispatch] = useReducer(dataReducer, dataInitialState)

  // フォローする
  const submitFollow = () => {
    postFollow({ userId: userId })
      .then(() => {
        setFollowing(true)
        alert('フォローしました')
      })
  }
  // フォローを解除する
  const submitUnfollow = () => {
    deleteUnfollow({ userId: userId })
      .then(() => {
        setFollowing(false)
        alert('フォローを解除しました')
      })
  }


  return (
    <>
      {following === true
        ? (
          <Button
            onClick={submitUnfollow}
            variant="contained"
          >
            フォロー中
          </Button>
        ) : (
          <Button
            onClick={submitFollow}
            variant="outlined"
          >
            フォローする
          </Button>
        )
      }
    </>
  )
}
