import React, { useReducer, useContext } from "react";
import { AuthContext } from "../../App";
//styled
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import Skeleton from '@material-ui/lab/Skeleton';
import Grid from "@mui/material/Grid";
// アイコン
import PermIdentityIcon from '@mui/icons-material/PermIdentity';
// api
import { postLogIn, postSignUp } from '../../apis/sessions';
// reducer
import { dialogInitialauthState, dialogReducer } from '../../reducer/DialogReducer';
// コンポーネント
import { SignUpDialog } from "../../components/Dialogs/SignUpDialog";
import { LogInDialog } from "../../components/Dialogs/LogInDialog";
import { PasswordResetDialog } from "../../components/Dialogs/PasswordResetDialog";
import { Typography } from "@mui/material";

export const Home = (props) => {
  // const initialauthState = {
  //   name: '',
  //   email: '',
  //   password: '',
  //   passwordConfirmation: '',
  //   rememberMe: '1',
  //   openSignup: false,
  //   openLogin: false,
  //   openPasswordReset: false,
  // };
  // const [authState, setauthState] = useauthState(initialauthState);
  const { authState, authDispatch } = useContext(AuthContext);
  const handleClose = () => authDispatch({ type: 'close' })

  const handleLogin = (data) => {
    authDispatch({
      type: 'login',
      payload: data.user,
    })
  }

  const submitLogin = () => {
    postLogIn({
      email: authState.email,
      password: authState.password,
      remember_me: authState.remenberMe,
    }).then(data => {
      handleLogin(data)
      handleClose()
    }).catch(() => {
      alert('ログイン失敗')
    })
  }

  const submitSignup = () => {
    postSignUp({
      name: authState.name,
      email: authState.email,
      password: authState.password,
      password_confirmation: authState.passwordConfirmation,
    }).then(data => {
      handleLogin(data)
      handleClose()
    }).catch(() => {
      alert('登録失敗')
    })
  }


  // ホームへ返す
  return (
    <>
      <Box sx={{ overflow: 'hidden' }}>

        <Box sx={{
          display: 'flex',
          justifyContent: 'center',
        }}>
          <Skeleton variant="rect" width={400} height={200} />
        </Box>

        <Box sx={{
          display: 'flex',
          flexDirection: 'row',
          justifyContent: 'center',
          my: 2
        }}
        >
          <Button onClick={() => authDispatch({ type: 'openSignup' })}>
            新規登録
          </Button>
          <Button onClick={() => authDispatch({ type: 'openLogin' })}>
            ログイン
          </Button>

          <SignUpDialog
            handleClose={handleClose}
            handleSubmit={submitSignup}
          />
          <LogInDialog
            open={authState.openLogin}
            handleClose={handleClose}
            handleLogIn={submitLogin}
            handlePasswordReset={() => authDispatch({ type: 'openPasswordReset' })}
          />
          <PasswordResetDialog
            open={authState.openPasswordReset}
            handleClose={handleClose}
          />
        </Box>

        <Box sx={{
          display: 'flex',
          flexWrap: 'wrap',
          justifyContent: 'center',

        }}>

          <Grid item sm={3} sx={{ display: 'flex', justifyContent: 'center' }}>
            <Button variant="contained" sx={{ width: 200, height: 200, p: 4 }}>
              <PermIdentityIcon />
              <Typography variant="h5">日記</Typography>
            </Button>
          </Grid>
          <Grid item sm={3} sx={{ display: 'flex', justifyContent: 'center' }}>
            <Button variant="contained" sx={{ width: 200, height: 200, p: 4 }}>
              <PermIdentityIcon />
              <Typography variant="h5">タイムライン</Typography>
            </Button>
          </Grid>
          <Grid item sm={3} sx={{ display: 'flex', justifyContent: 'center' }}>
            <Button variant="contained" sx={{ width: 200, height: 200, p: 4 }}>
              <PermIdentityIcon />
              <Typography variant="h5">睡眠本</Typography>
            </Button>
          </Grid>
          <Grid item sm={3} sx={{ display: 'flex', justifyContent: 'center' }}>
            <Button variant="contained" sx={{ width: 200, height: 200, p: 4 }}>
              <PermIdentityIcon />
              <Typography variant="h5">ニュース</Typography>
            </Button>
          </Grid>
          <Grid item sm={3} sx={{ display: 'flex', justifyContent: 'center' }}>
            <Button variant="contained" sx={{ width: 200, height: 200, p: 4 }}>
              <PermIdentityIcon />
              <Typography variant="h5">フォロー</Typography>
            </Button>
          </Grid>
          <Grid item sm={3} sx={{ display: 'flex', justifyContent: 'center' }}>
            <Button variant="contained" sx={{ width: 200, height: 200, p: 4 }}>
              <PermIdentityIcon />
              <Typography variant="h5">メッセージ</Typography>
            </Button>
          </Grid>
          <Grid item sm={3} sx={{ display: 'flex', justifyContent: 'center' }}>
            <Button variant="contained" sx={{ width: 200, height: 200, p: 4 }}>
              <PermIdentityIcon />
              <Typography variant="h5">プロフィール</Typography>
            </Button>
          </Grid>

        </Box>

      </Box>
    </>
  )
}
