import React, { useState } from "react"
import { Link, useHistory } from "react-router-dom";
// style
import { Avatar, ListItem, Typography } from "@mui/material";
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
// アイコン
import AccountCircle from "@mui/icons-material/AccountCircle";
// コンポーネント
import { SettingDialog } from "../Dialogs/SettingDialog";
import { FollowButton } from "../Buttons/FollowButton";

export const UserInfo = ({
  loginUser,
  profile,
}) => {
  // 設定ダイアログの開閉
  const history = useHistory();
  const [open, setOpen] = useState(false);

  return (
    <>
      <Box sx={{
        display: 'flex',
        alignItems: 'center',
      }}>
        <Avatar sx={{
          alignItems: 'center',
          width: 100, height: 100,
          p: 4,
        }} >
          <AccountCircle sx={{ fontSize: 100 }} />
        </Avatar>
        <Box sx={{ pl: 3 }}>
          <Typography variant="h4">
            <Box sx={{ letterSpacing: 6, my: 2 }}>{profile.user.name}</Box>
          </Typography>
          <Typography variant="body1">
            <Box sx={{ letterSpacing: 3, my: 2 }}>{profile.user.profile}</Box>
          </Typography>
          <Box sx={{ display: 'flex' }}>
            <Typography variant="body1">
              <Box
                onClick={() => history.push(`/users/${profile.user.id}/following`)}
                sx={{ letterSpacing: 2, mr: 4, cursor: 'pointer' }}
              >
                <b>{profile.followingIds.length}</b> フォロー中
              </Box>
            </Typography>
            <Typography
              variant="body1"
              sx={{ flexGrow: 1 }}
            >
              <Box
                onClick={() => history.push(`/users/${profile.user.id}/followers`)}
                sx={{ letterSpacing: 2, cursor: 'pointer' }}
              >
                <b>{profile.followersIds.length}</b> フォロワー
              </Box>
            </Typography>
            {
              (loginUser.id === profile.user.id) ? (
                <Button onClick={() => { setOpen(true) }}>
                  プロフィール編集
                </Button>
              ) : (
                <FollowButton
                  userId={profile.user.id}
                  followStatus={profile.followStatus}
                />
              )
            }
          </Box>
        </Box>
      </Box>

      <SettingDialog
        handleClose={() => { setOpen(false) }}
        open={open}
        dataUserFetch={profile.dataUserFetch}
      />
    </>
  )
}
