import React, { Fragment, useState, useEffect } from "react";
import Link from '@mui/material/Link';
// ユーザーページのstyle
import { Avatar, ListItemAvatar, Typography } from "@mui/material";
import Button from '@mui/material/Button';
import { Grid, ListItem } from "@mui/material";
import List from '@mui/material/List';
import ListItemText from '@mui/material/ListItemText';
// アイコン
import AccountCircle from "@mui/icons-material/AccountCircle";
// api
import { fetchUser } from "../apis/users";
import { deleteMicropost } from "../apis/microposts";
// コンポーネント
import { SettingDialog } from "../components/SettingDialog";
import { MicropostDialog } from "../components/MicropostDialog";

export const User = (props) => {
  const [openSettingDialog, setOpenSettingDialog] = useState(false)
  const [openDialogPost, setOpenDialogPost] = useState(false)
  const [microposts, setMicroposts] = useState([])
  // 設定変更Dialogを開閉する関数群
  const handleOpenSetting = () => { setOpenSettingDialog(true) }
  const handleCloseSetting = () => { setOpenSettingDialog(false) }
  // 投稿Dialogを開閉する関数群
  const handleOpenPost = () => { setOpenDialogPost(true) }
  const handleClosePost = () => { setOpenDialogPost(false) }
  // 投稿一覧を表示する画面
  const MicropostsList = microposts.map(micropost =>
    <ListItem key={micropost.id}>
      <ListItemAvatar>
        <AccountCircle sx={{ fontSize: 40 }} />
      </ListItemAvatar>
      <div>
        <ListItemText
          component="div"
          primary={micropost.user_id}
          secondary={micropost.created_at}
        />
        {props.user.id === micropost.user_id && (
          <Link component="div" onClick={() => deleteMicropost(micropost.id)}>delete</Link>
        )}
      </div>
      <Typography variant="body1" pl={2}>
        {micropost.content}
      </Typography>
    </ListItem >
  )
  // レンダーされた際に投稿内容を描画する。※未完成
  useEffect(() => {
    fetchUser({ user_id: props.user.id })
      .then(data => {
        setMicroposts(data.microposts)
      })
    return setMicroposts([])
  }, [])

  // 返り値：ユーザー画面
  return (
    <Grid container sx={{ width: 1000, mx: "auto", bgcolor: 'grey.300' }}>
      <Grid item xs={12} sm={4} sx={{ px: 2, bgcolor: 'grey.200' }}>
        <h2>プロフィール</h2>
        <Avatar sx={{ width: 56, height: 56 }} >
          <AccountCircle sx={{ fontSize: 65 }} />
        </Avatar>
        <Typography variant="h6">
          {props.user.name}
        </Typography>
        <Typography variant="body1">
          プロフィールを追加予定
        </Typography>
        {
          props.isLoggedIn &&
          <Fragment>
            <div>
              <Button onClick={handleOpenSetting}>
                プロフィールの編集
              </Button>
              <SettingDialog
                handleClose={handleCloseSetting}
                open={openSettingDialog}
                user={props.user}
              />
            </div>
            <div>
              <Button variant="contained" onClick={handleOpenPost}>
                投稿
              </Button>
            </div>
          </Fragment>
        }
        <MicropostDialog
          handleClose={handleClosePost}
          open={openDialogPost}
          user={props.user}
        />
      </Grid>
      <Grid item xs={12} sm={8} sx={{ px: 2, bgcolor: 'grey.100' }}>
        <div>
          <h2>投稿一覧</h2>
          <List sx={{ width: '100%', bgcolor: 'background.paper' }}>
            {MicropostsList}
          </List>
        </div>
      </Grid>
    </Grid>
  )
}
