export const postInitialState = {
  content: '',
  open: false,
}

export const postReducer = (state, action) => {
  switch (action.type) {
    case 'content':
      return {
        ...state,
        content: action.payload,
      };
    case 'open':
      return {
        ...state,
        open: true,
      };
    case 'reset':
      return {
        content: '',
        open: false,
      };
    case 'close':
      return {
        ...state,
        open: false,
      };
    default:
      return postInitialState;
  }
}
