import React, { useState } from 'react';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import TextField from '@mui/material/TextField';

// 入力フォーム
import { postSignUp } from '../apis/users';


export const SignUpDialog = ({ open, handleClickOpen, handleClose }) => {
  const [name, setName] = useState('')
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [passwordConfirmation, setpasswordConfirmaiton] = useState('')

  const handleSubmit = () => {
    alert('送信完了')
    console.log(name, email, password, passwordConfirmation);
    handleClose();
  }

  return (
    <div>


      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>新規登録</DialogTitle>
        <DialogContent>
          <DialogContentText>
            下記項目を入力し、登録をお願いします。
          </DialogContentText>

          <TextField autoFocus margin="dense" id="name" label="名前" type="text"
            required value={name} fullWidth variant="standard" />
          <TextField autoFocus margin="dense" id="email" label="E-mail" type="email"
            required value={email} fullWidth variant="standard" />
          <TextField autoFocus margin="dense" id="password" label="パスワード" type="password"
            required value={password} fullWidth variant="standard" />
          <TextField autoFocus margin="dense" id="password_confirmation" label="パスワードの確認" type="password"
            required value={passwordConfirmation} fullWidth variant="standard" />

        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>キャンセル</Button>
          <Button onClick={handleSubmit}>新規登録</Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
