import React, { useReducer, useContext } from 'react';
import { Link, useHistory } from "react-router-dom";
import { AuthContext } from "../../App";
// Style
import { styled } from '@mui/material/styles';
import MuiAppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import IconButton from '@mui/material/IconButton';
import Button from '@mui/material/Button';
// アイコン
import MenuIcon from '@mui/icons-material/Menu';
// Api
import { postLogIn } from '../../apis/sessions';
import { deleteLogout } from "../../apis/sessions"
import { postMicropost } from '../../apis/microposts';
// Reducer
import { dialogReducer, dialogInitialState } from '../../reducer/DialogReducer'
// Component
import { LoginControlBottun } from '../../components/Buttons/LoginControlButton'
import { MicropostDialog } from '../../components/Dialogs/MicropostDialog';

export const Header = (props) => {
  const history = useHistory()

  const { authState, authDispatch } = useContext(AuthContext);

  const handleClose = () => authDispatch({ type: 'close' })

  const handleLogin = (data) => {
    authDispatch({
      type: 'login',
      payload: data.user,
    });
  };

  const handleLogout = () => {
    authDispatch({
      type: 'logout',
    });
  };

  const submitLogin = () => {
    postLogIn({
      email: authState.email,
      password: authState.password,
      remember_me: authState.remenberMe,
    }).then(data => {
      handleLogin(data);
      handleClose();
    });
  };

  const submitLogout = () => {
    deleteLogout()
      .then(() => {
        handleLogout();
        history.push(`/`);
        alert('ログアウトを成功しました');
      });
  };

  const [dialogState, dialogDispatch] = useReducer(dialogReducer, dialogInitialState);

  const open = props.open

  const AppBar = styled(MuiAppBar, {
    shouldForwardProp: (prop) => prop !== 'open',
  })(({ theme, open }) => ({
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    ...(open && {
      marginLeft: props.drawerWidth,
      width: `calc(100% - ${props.drawerWidth}px)`,
      transition: theme.transitions.create(['width', 'margin'], {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.enteringScreen,
      }),
    }),
  }));

  return (
    <AppBar position="fixed" open={open}>
      <Toolbar>
        <IconButton
          color="inherit"
          aria-label="open drawer"
          onClick={() => props.handleDrawerOpen()}
          edge="start"
          sx={{
            marginRight: '36px',
            ...(open && { display: 'none' }),
          }}
        >
          <MenuIcon
            sx={{
              marginRight: '36px',
              ...(open && { display: 'none' }),
            }}
          />
        </IconButton>
        <Typography variant="h6" component="div" flexGrow={1}
          sx={open && { display: 'none' }}
        >
          睡眠負債
        </Typography>
        <Button
          variant="body1"
          onClick={() => dialogDispatch({ type: 'micropost' })}
        >
          投稿
        </Button>
        <MicropostDialog
          open={dialogState.micropost}
          handleClose={() => dialogDispatch({ type: 'close' })}
        />
        <Typography
          sx={{ px: 4 }}
          variant="body1"
          component={Link}
          to={`/booksearch`}
        >
          本の検索
        </Typography>
        <LoginControlBottun
          handleLogOut={submitLogout}
          handleLogIn={submitLogin}
          loginUser={authState.loginUser}
          isLoggedIn={authState.loggedIn}
        />
      </Toolbar>
    </AppBar>
  )
}
