import React, { Fragment, useState, useEffect } from "react";
// ユーザーページのstyle
import Button from '@mui/material/Button';
// api
import { fetchUser } from "../apis/users";
// コンポーネント
import { SettingDialog } from "../components/SettingDialog";
import { MicropostDialog } from "../components/MicropostDialog";
import { ListItem } from "@mui/material";
import ListItemText from '@mui/material/ListItemText';


export const User = (props) => {
  const [openSettingDialog, setOpenSettingDialog] = useState(false)
  const [openDialogPost, setOpenDialogPost] = useState(false)
  const [microposts, setMicroposts] = useState([])
  // 設定変更Dialogを開閉する関数
  const handleOpenSetting = () => { setOpenSettingDialog(true) }
  const handleCloseSetting = () => { setOpenSettingDialog(false) }
  // 投稿Dialogを開閉する関数
  const handleOpenPost = () => { setOpenDialogPost(true) }
  const handleClosePost = () => { setOpenDialogPost(false) }
  // 投稿一覧の画面
  const MicropostsList = microposts.map((micropost) =>
    // <li key={micropost.id}>
    //   <h3>{micropost.content}</h3>
    //   {/* <p>{micropost.user.name}</p> */}
    //   <p>{micropost.created_at}</p>
    //   {
    //     props.user &&
    //     <p >delete</p>
    //   }
    // </li >
    <ListItemText primary={micropost.content} secondary={micropost.created_at} />
  )

  // 投稿内容が更新した際にレンダーする
  useEffect(() => {
    fetchUser(1)
      .then(data => {
        setMicroposts(data.microposts)
      })
  }, [])

  // 返り値：ユーザー画面
  return (
    <Fragment>
      <h1>My Profile</h1>
      <div className="my_profile">
        <p>ID:{props.user.id}</p>
        <p>名前:{props.user.name}</p>
        <p>※プロフィールの追加</p>
        <p>※画像の追加</p>
        {
          props.isLoggedIn &&
          <div>
            <Button variant="outlined" onClick={handleOpenSetting}>
              設定（ユーザー情報の更新）
            </Button>
            <SettingDialog
              handleClose={handleCloseSetting}
              open={openSettingDialog}
              user={props.user}
            />
          </div>
        }
      </div>
      <Button variant="outlined" onClick={handleOpenPost}>
        投稿
      </Button>
      <MicropostDialog
        handleClose={handleClosePost}
        open={openDialogPost}
        user={props.user}
      />
      <div>
        <h1>投稿一覧</h1>
        {/* <ul>{MicropostsList}</ul> */}
        <ListItem>{MicropostsList}</ListItem>
      </div>

    </Fragment>
  )
}
