import React from "react";
// styles
import Button from "@mui/material/Button";
// api
import { postRoom } from "../../apis/room";

export const RoomButton = (props) => {
  // メッセージルームを作成する
  const handleSubmit = () => {
    postRoom({ userId: props.userId })
  }

  return (
    <Button
      onClick={handleSubmit}
      variant="contained"
    >
      メッセージを送る
    </Button>
  )
}
