import React, { useState } from "react"
import { Link } from "react-router-dom";
// style
import { Avatar, ListItem, Typography } from "@mui/material";
import Button from '@mui/material/Button';
import { Grid } from "@mui/material";
// アイコン
import AccountCircle from "@mui/icons-material/AccountCircle";
// コンポーネント
import { SettingDialog } from "./Dialogs/SettingDialog";
import { MicropostDialog } from "./Dialogs/MicropostDialog";
import { FollowButton } from "./Buttons/FollowButton";

export const MyProfile = (props) => {

  return (
    <>
      <h2>マイプロフィール</h2>
      <Grid container sx={{ mx: "auto", bgcolor: 'grey.300' }}>
        <Grid item xs={12} sm={4} sx={{ px: 2, bgcolor: 'grey.200' }}>

          <Avatar sx={{ width: 56, height: 56 }} >
            <AccountCircle sx={{ fontSize: 65 }} />
          </Avatar>
          <Typography variant="h6">
            {props.user.name}
          </Typography>
          <Typography variant="body1">
            props.user.profile.プロフィールを追加予定。ここにプロフィールが追加されます。ユーザーのプロフィールです。
            ユーザーの自己紹介となります。趣味などが記載されます。
          </Typography>
          <Grid container spacing={2}>
            <Grid item sm={6}>
              <ListItem
                component={Link}
                to={`/users/${props.user.id}/following`}
              >
                {props.following.length} フォロー中
              </ListItem>
            </Grid>
            <Grid item sm={6}>
              <ListItem
                component={Link}
                to={`/users/${props.user.id}/followers`}
              >
                {props.followers.length} フォロワー
              </ListItem>
            </Grid>
          </Grid>
          <Grid item sm={12}>
            <ListItem
              component={Link}
              to={`/users/${props.user.id}/microposts`}
            >
              {props.microposts.length} つぶやき
            </ListItem>
          </Grid>
        </Grid>
      </Grid>
    </>
  )
}
