import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { postLogIn } from '../apis/users';

// ダイアログのstyle
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import TextField from '@mui/material/TextField';
import { Email } from './Forms/Email';


export const LogInDialog = (props) => {

  const [password, setPassword] = useState('')
  const history = useHistory()

  // ログインapiを呼び出すCallback関数
  const handleSubmit = () => {
    postLogIn({
      email: email,
      password: password,
    }).then((data) => {
      if (data.status === 'created') {
        props.handleLogin(data)
        history.push(`/user/${data.user.id}`)
      }
      else
        alert('メールアドレスまたはパスワードに誤りがあります');
    })
  };

  // 返り値：新規登録ダイアログの内容を返す
  return (
    <Dialog
      open={props.open}
      onClose={props.handleClose}
    >
      <DialogTitle>
        ログイン
      </DialogTitle>
      <DialogContent>
        <DialogContentText>
          下記項目を入力し、ログインください。
        </DialogContentText>
        <Email
        />
        <TextField
          autoFocus
          margin="dense"
          id="password"
          label="パスワード"
          type="password"
          required
          value={password}
          onChange={e => setPassword(e.target.value)}
          fullWidth
          variant="standard"
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={props.handleClose}>キャンセル</Button>
        <Button type='submit' onClick={handleSubmit} autoFocus>ログイン</Button>
      </DialogActions>

    </Dialog>
  );
}
