import axios from "axios";
import { roomCreate, roomDestroy } from "../urls";

// メッセージルームを作成するapi
export const postRoom = (params) => {
  return axios.post(roomCreate, {
    room: { user_id: params.userId }
  },
    { withCredentials: true }
  )
    .then(res => {
      if (res.data) {
        console.log('room#create', res);
        return res.data;
      }
    })
    .catch(error => {
      console.log('room#create', error)
    })
}

// メッセージルームを削除するapi
export const deletePost = (roomId) => {
  return axios.delete(roomDestroy(roomId), { withCredentials: true })
    .then(res => {
      console.log('room#destroy', res)
      alert('投稿を削除しました')
      return res.data
    })
    .catch(error => {
      console.log('room#destroy', error)
    })
}
