import React, { useEffect, useReducer, createContext } from 'react';
import './App.css';
// Style
import { createTheme, ThemeProvider } from '@material-ui/core/styles';
import Box from '@mui/material/Box';
import CssBaseline from '@mui/material/CssBaseline';
// import { darkTheme } from './styled/theme'
// Api
import { fetchLoggedIn } from './apis/sessions';
// Reducer
import { authInitialState, authReducer } from './reducer/AuthReducer'
// Container
import { Layout } from './containers/Layout';

export const AuthContext = createContext()

export default function App() {
  const [authState, authDispatch] = useReducer(authReducer, authInitialState)
  const darkTheme = createTheme({
    palette: {
      mode: 'dark',
      primary: {
        main: '#ce93d8',
        ligth: '#f3e5f5',
        dark: '#ab47bc',
      },
    },
  });

  useEffect(() => {
    fetchLoggedIn()
      .then(data => {
        if (data.logged_in && authState.loggedIn === false) {
          authDispatch({
            type: 'login',
            payload: data.user,
          })
        } else if (!data.logged_in && authState.loggedIn === true) {
          authDispatch({
            type: 'logout',
          })
        }
      })
  }, [])

  return (
    <Box sx={{ display: 'flex' }}>
      <ThemeProvider theme={darkTheme}>
        <CssBaseline />
        <AuthContext.Provider value={{ authState, authDispatch }}>
          <Layout />
        </AuthContext.Provider>
      </ThemeProvider>
    </Box>
  );
}
