  //     })
  // }, [])

  return (
    <>
      <FullCalendar
        plugins={[dayGridPlugin]}
        initialView="dayGridMonth"
        locale="ja"
        events={[
          { title: "event 1", start: "2021-07-01" },
          { title: "event 2", start: "2021-07-02T12:00:00" }
        ]}
      />

      <Button variant="inherit" onClick={handleOpen}>
        記録する
      </Button>
      <DiaryDialog
        open={open}
        handleClose={handleClose}
      />
      {/* <p>{diaries[0]}</p> */}
    </>
  )
}
