import React from "react";
import { Button } from "@mui/material";
import axios from "axios";
import { postFollow } from "../../apis/relationships";

export const FollowButton = (props) => {
  const handleClick = () => {
    return axios(postFollow({ userId: props.users.id })
      .then(data => {
        props.handleFollow(data.follow_status)
      }))
  }

  return (
    <Button
      fullWidth="true"
      onClick={handleClick}
      p={2}
      variant="contained"
    >
      フォローする
    </Button>
  )
}
