import React, { useEffect, useState, useReducer } from "react";
import { useCallback } from 'react';
// Style
import { Emoji } from 'emoji-mart';
import FullCalendar, { EventContentArg } from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin, { DateClickArg } from '@fullcalendar/interaction';
import Box from '@mui/material/Box';
// Api
import { fetchUserDiaries } from "../../apis/users";
// Reducer
import { dialogReducer, dialogInitialState } from '../../reducer/DialogReducer'
// Component
import { DiaryDialog } from "../Dialogs/DiaryDialog"

export const Calendar = ({
  userId,
  open,
}) => {
  const [diaries, setDiaries] = useState([])
  const [dialogState, dialogDispatch] = useReducer(dialogReducer, dialogInitialState);
  const handleClose = () => dialogDispatch({ type: 'close' })

  // カレンダーイベントの表記内容
  const renderEventContent = (eventInfo: EventContentArg) => (
    <Box sx={{
      textAlign: "center",
      pt: 1,
    }}>
      <Emoji
        emoji={eventInfo.event.title.feeling}
        size={24}
      />
      {/* <p><b>{eventInfo.event.title.sleeping_hours} h</b></p> */}
    </Box>
  )

  useEffect(() => {
    fetchUserDiaries(userId)
      .then(data => {
        setDiaries(data.diaries)
      })
  }, [open])


  return (
    <Box>
      <FullCalendar
        plugins={[dayGridPlugin, interactionPlugin]}
        initialView="dayGridMonth"
        locale="ja"
        events={diaries}
        eventClick={() => dialogDispatch({ type: 'diary' })}
        eventContent={renderEventContent}
        businessHours={true}
        dayCellContent={(e) => { e.dayNumberText = e.dayNumberText.replace('日', '') }}
      />


      < DiaryDialog
        handleClose={handleClose}
        open={dialogState.diary}
      // date={arg.dateStr}
      />
    </Box>
  )
}
