import React, { useState, useReducer, useContext } from "react";
import { AuthContext } from "../../App";
//styled
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import Skeleton from '@material-ui/lab/Skeleton';
import Grid from "@mui/material/Grid";
// アイコン
import PermIdentityIcon from '@mui/icons-material/PermIdentity';
// api
import { postLogIn } from '../../apis/sessions';
// reducer
import { dialogInitialState, dialogReducer } from '../../reducer/DialogReducer';
// コンポーネント
import { SignUpDialog } from "../../components/Dialogs/SignUpDialog";
import { LogInDialog } from "../../components/Dialogs/LogInDialog";
import { PasswordResetDialog } from "../../components/Dialogs/PasswordResetDialog";
import { Typography } from "@mui/material";

export const Home = (props) => {
  const initialState = {
    name: '',
    email: '',
    password: '',
    passwordConfirmation: '',
    rememberMe: '1',
    openSignup: false,
    openLogin: false,
    openPasswordReset: false,
  };
  const [state, setState] = useState(initialState);
  const { authDispatch } = useContext(AuthContext);

  const handleLogin = (data) => {
    authDispatch({
      type: 'login',
      payload: data.user,
    })
  }

  const submitLogin = () => {
    postLogIn({
      email: state.email,
      password: state.password,
      remember_me: state.remenberMe,
    }).then(data => {
      handleLogin(data)
      setState({ openLogin: false })
    }).catch(() => {
      alert('ログイン失敗')
    })
  }

  const submitSignup = () => {
    postSignUp({
      name: state.name,
      email: state.email,
      password: state.password,
      password_confirmation: state.passwordConfirmation,
    }).then(data => {
      handleLogin(data)
      setState({ openSignup: false })
    }).catch(() => {
      alert('登録失敗')
    })
  }


  // ホームへ返す
  return (
    <>
      <Box sx={{ overflow: 'hidden' }}>

        <Box sx={{
          display: 'flex',
          justifyContent: 'center',
        }}>
          <Skeleton variant="rect" width={400} height={200} />
        </Box>

        <Box sx={{
          display: 'flex',
          flexDirection: 'row',
          justifyContent: 'center',
          my: 2
        }}
        >
          <Button onClick={() => setState({ openSignup: true })}>
            新規登録
          </Button>
          <Button onClick={() => setState({ openLogin: true })}>
            ログイン
          </Button>

          <SignUpDialog
            open={state.openSignup}
            handleClose={() => setState({ openSignup: false })}
            handleSubmit={submitSignup}
            user={state}
            name={state.name}
            email={state.email}
            password={state.password}
            passwordConfirmation={state.passwordConfirmation}
            inputName={setName}
            inputEmail={setEmail}
            inputPassword={setPassword}
            inputPasswordConfirmation={setPasswordConfirmation}
          />
          <LogInDialog
            open={state.openLogin}
            handleClose={() => setState({ openLogin: false })}
            handleLogIn={submitLogin}
            handlePasswordReset={() =>
              setState({
                openLogin: false,
                openPasswordReset: false,
              })}
          />
          <PasswordResetDialog
            open={state.openPasswordReset}
            handleClose={() => setState({ openPasswordReset: false })}
          />
        </Box>

        <Box sx={{
          display: 'flex',
          flexWrap: 'wrap',
          justifyContent: 'center',

        }}>

          <Grid item sm={3} sx={{ display: 'flex', justifyContent: 'center' }}>
            <Button variant="contained" sx={{ width: 200, height: 200, p: 4 }}>
              <PermIdentityIcon />
              <Typography variant="h5">日記</Typography>
            </Button>
          </Grid>
          <Grid item sm={3} sx={{ display: 'flex', justifyContent: 'center' }}>
            <Button variant="contained" sx={{ width: 200, height: 200, p: 4 }}>
              <PermIdentityIcon />
              <Typography variant="h5">タイムライン</Typography>
            </Button>
          </Grid>
          <Grid item sm={3} sx={{ display: 'flex', justifyContent: 'center' }}>
            <Button variant="contained" sx={{ width: 200, height: 200, p: 4 }}>
              <PermIdentityIcon />
              <Typography variant="h5">睡眠本</Typography>
            </Button>
          </Grid>
          <Grid item sm={3} sx={{ display: 'flex', justifyContent: 'center' }}>
            <Button variant="contained" sx={{ width: 200, height: 200, p: 4 }}>
              <PermIdentityIcon />
              <Typography variant="h5">ニュース</Typography>
            </Button>
          </Grid>
          <Grid item sm={3} sx={{ display: 'flex', justifyContent: 'center' }}>
            <Button variant="contained" sx={{ width: 200, height: 200, p: 4 }}>
              <PermIdentityIcon />
              <Typography variant="h5">フォロー</Typography>
            </Button>
          </Grid>
          <Grid item sm={3} sx={{ display: 'flex', justifyContent: 'center' }}>
            <Button variant="contained" sx={{ width: 200, height: 200, p: 4 }}>
              <PermIdentityIcon />
              <Typography variant="h5">メッセージ</Typography>
            </Button>
          </Grid>
          <Grid item sm={3} sx={{ display: 'flex', justifyContent: 'center' }}>
            <Button variant="contained" sx={{ width: 200, height: 200, p: 4 }}>
              <PermIdentityIcon />
              <Typography variant="h5">プロフィール</Typography>
            </Button>
          </Grid>

        </Box>

      </Box>
    </>
  )
}
