import React, { useState, useEffect } from "react";
import axios from "axios";
// styles
import Button from "@mui/material/Button";
//api
import { postFollow } from "../../apis/relationships";
import { deleteUnfollow } from "../../apis/relationships";
import { fetchFollow } from "../../apis/relationships";


export const FollowButton = ({ userId, followingIds }) => {
  const InitiateFollowStatus = followingIds.includes(userId)
  const [followStatus, setFollowStatus] = useState(InitiateFollowStatus)

  // フォローする
  const submitFollow = () => {
    postFollow({ userId: userId })
      .then(setFollowStatus(true))
  }
  // フォローを解除する
  const submitUnfollow = () => {
    deleteUnfollow({ relationshipId: userId })
      .then(setFollowStatus(false))
  }


  return (
    <>
      {followStatus === true ? (<Button
        onClick={submitUnfollow}
        variant="contained"
      >
        フォロー中
      </Button>
      ) : (
        <Button
          onClick={submitFollow}
          variant="contained"
        >
          フォローする{followingIds.length[1]}
        </Button>
      )
      }
    </>
  )
}
