import React from 'react';
// styles
import { styled } from '@mui/material/styles';
import MuiAppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import IconButton from '@mui/material/IconButton';
// アイコン
import MenuIcon from '@mui/icons-material/Menu';
// コンポーネント
import { LoginControlBottun } from '../../components/Buttons/LoginControlButton'

export const Header = (open, theme, drawerWidth, handleDrawerOpen) => {

  const AppBar = styled(MuiAppBar, {
    shouldForwardProp: (prop) => prop !== 'open',
  })(({ theme, open }) => ({
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    ...(open && {
      marginLeft: drawerWidth,
      width: `calc(100% - ${drawerWidth}px)`,
      transition: theme.transitions.create(['width', 'margin'], {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.enteringScreen,
      }),
    }),
  }));

  const [openLogInDialog, setOpenLogInDialog] = useState(false)
  // ログインDialogを開閉する関数群
  const handleOpenLogIn = () => setOpenLogInDialog(true)
  const handleCloseLogIn = () => setOpenLogInDialog(false)

  return (
    <AppBar position="fixed" open={open}>
      <Toolbar>
        <IconButton
          color="inherit"
          aria-label="open drawer"
          onClick={handleDrawerOpen}
          edge="start"
          sx={{
            marginRight: '36px',
            ...(open && { display: 'none' }),
          }}
        >
          <MenuIcon />
        </IconButton>
        <Typography variant="h6" noWrap component="div" flexGrow={1}>
          睡眠負債
        </Typography>
        <LoginControlBottun
          handleCloseLogIn={handleCloseLogIn}
          handleLogOut={props.handleLogOut}
          handleLogIn={props.handleLogIn}
          handleOpenLogIn={handleOpenLogIn}
          loginUser={props.loginUser}
          isLoggedIn={props.isLoggedIn}
          open={openLogInDialog}
        />
      </Toolbar>
    </AppBar>
  )
}
