import React, { useState, useEffect } from "react";
import axios from "axios";
// styles
import Button from "@mui/material/Button";
//api
import { postFollow } from "../../apis/relationships";
import { deleteUnfollow } from "../../apis/relationships";
import { fetchFollow } from "../../apis/relationships";


export const FollowButton = (props) => {
  const userId = props.userId
  const InitiateFollowStatus = props.followersIds.include(userId)
  const [followStatus, setFollowStatus] = useState(InitiateFollowStatus)

  // フォローする
  const submitFollow = () => {
    postFollow({ userId: userId })
      .then(setFollowStatus(true))
  }
  // フォローを解除する
  const submitUnfollow = () => {
    deleteUnfollow({ relationshipId: userId })
      .then(setFollowStatus(false))
  }

  // 連続してfetchするので一旦中止。改善予定
  // useEffect(() => {
  //   // フォロー有無を確認する
  //   fetchFollow({ userId: props.userId })
  //     .then(data => setFollowStatus(data))
  // }, [])

  return (
    <>
      {(followStatus === true)
        ? <Button
          onClick={submitUnfollow}
          variant="contained"
        >
          フォロー中
        </Button>
        : <Button
          onClick={submitFollow}
          variant="contained"
        >
          フォローする
        </Button>
      }
    </>
  )
}
