import React, { useContext, useReducer } from "react";
import { AuthContext } from '../../App';
// Style
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import { makeStyles } from '@material-ui/styles';
import Typography from "@mui/material/Typography";
// Reducer
import { dialogReducer, dialogInitialState } from '../../reducer/DialogReducer';
// Component
import { Calendar } from "../../components/UserInfomation/Calendar";
import { SleepInfo } from "../../components/UserInfomation/SleepInfo";
// Dialog
import { RecordDialog } from "../../components/Dialogs/RecordDialog";
import { SleepData } from "../../components/UserInfomation/SleepData";
// import { Loading } from '../../components/Loading';

const useStyles = makeStyles(() => ({
  root: {
    alignItems: 'center',
    flexDirection: 'column',
    flexWrap: 'wrap',
    justifyContent: 'center',
    maxWidth: 1000,
    mx: 'auto',
    textAlign: 'center',
    width: '100%',
  },
}));

export const Diaries = ({ userId }) => {
  const classes = useStyles();
  const [dialogState, dialogDispatch] = useReducer(dialogReducer, dialogInitialState);

  // 睡眠負債を取得する

  return (
    <Box className={classes.root}>
      <Typography variant="h3">
        <Box sx={{ letterSpacing: 10 }}><b>~ 睡眠日記 ~</b></Box>
      </Typography>
      <Grid container>
        <Grid item xs={12} sm={6} sx={{
          alignItems: "center",
          justifyContent: 'center',
          mt: 4,
        }}>
          <SleepInfo
            userName={'Testの名前'}
            sleepDebt={'99'}
            sleepSaving={'99'}
          />
          <Box sx={{
            alignItems: "center",
            justifyContent: 'center',
          }}>
            <Typography variant="h3">
              <Box sx={{ letterSpacing: 6 }}>理想睡眠時間：<b>{'7.0'} 時間</b></Box>
            </Typography>
            <Button onClick={() => dialogDispatch({ type: 'diary' })} >
              睡眠日記を書く
            </Button>
          </Box>
        </Grid>

        <Grid item sm={12} md={6}
          sx={{ mt: 4, pr: 2 }}
        >
          <Calendar
            userId={userId}
            open={dialogState.diary}
          />
        </Grid>
      </Grid>
      <Box>
        {/* {diaryState.fetchState != 'ok' ? <Loading /> : */}
        <SleepData
        />
        {/* } */}
      </Box>
      <RecordDialog
        handleClose={() => dialogDispatch({ type: 'close' })}
        open={dialogState.diary}
      />
    </Box>
  )
}
