import React, { useEffect, useState } from "react";
//Style
import Box from '@mui/material/Box';
import Button from "@mui/material/Button";
import { createStyles, makeStyles } from "@material-ui/core/styles";
import Divider from '@mui/material/Divider';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
// Api
import { fetchBook } from "../../apis/books";
// Component
import { ReadButton } from "../../components/Buttons/ReadButton";

const useStyles = makeStyles(() =>
  createStyles({
    'root': {
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'center',
      maxWidth: 1000,
      mx: 'auto',
      textAlign: 'center',
      width: '100%',
    },
    'image': {
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'space-between',
      maxWidth: 1000,
      mx: 'auto',
      textAlign: 'center',
      width: '100%',
    },
  }),
);

export const Book = ({ bookIsbn }) => {
  const classes = useStyles();
  const [book, setBook] = useState([])
  const [registration, setResistration] = useState(false)
  const [subscribed, setSubscribed] = useState(false)

  // ISBNで特定した本情報を取得する
  useEffect(() => {
    fetchBook(bookIsbn)
      .then(data => {
        setBook(data.book[0].params)
        setResistration(data.registration)
        setSubscribed(data.subscribed)
      })
  }, [])

  return (
    <Box className={classes.root}>
      <Typography variant="h4" sx={{ width: '100%' }}>
        <Box sx={{ letterSpacing: 10, pb: 2 }}><b>{book.title}</b></Box>
      </Typography>
      <Divider />
      <Box sx={{ width: 300 }}>
        <CardMedia
          component="img"
          image={book.largeImageUrl}
          sx={{ width: '100%' }}
          alt={book.title}
        />
      </Box>
      <Box>
        <Box>
          <p>著者名 ： {book.author}</p>
          <p>出版社名 ： {book.publisherName}</p>
          <p>出版日 ： {book.salesDate}</p>
          <p>定価  ： {book.itemPrice}円</p>
          <p>URL  ： {book.itemUrl}</p>
          <p>楽天レビュー平均 ： {book.reviewAverage}点</p>
          <p>楽天レビュー件数 ： {book.reviewCount}件</p>
          <Box>
            <ReadButton
              book={book}
              registration={registration}
              subscribed={subscribed}
            />
            <Button>
              私のおすすめにする
            </Button>
          </Box>
        </Box>
      </Box>
    </Box>
  )
}
