  // 新規登録ダイアログの内容を返す
  return (
    <Dialog
      open={open}
      onClose={handleClose}
    >
      <DialogTitle>
        {event.date.toString()}
        の睡眠日記
      </DialogTitle>
      <DialogContent>
        <Date
          date={recordState.date}
          handleChange={e => recordDispatch({
            type: 'date',
            payload: e.target.value
          })}
        />
        <SleepingHours
          sleepingHours={recordState.sleepingHours}
          handleChange={e => recordDispatch({
            type: 'sleepingHours',
            payload: e.target.value
          })
          }
        />

        <Feeling
          feeling={recordState.feeling}
          recordDispatch={recordDispatch}
        />

      </DialogContent>

      <DialogActions>
        <Button onClick={submitUpdate} type='submit'>
          編集する
        </Button>
      </DialogActions>
    </Dialog>
  );
}
