import React, { useEffect, useState } from "react";
import { useCallback } from 'react';
// styles
// import Grid from "@mui/material/Grid";
import Button from "@mui/material/Button";
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin, { DateClickArg } from '@fullcalendar/interaction';
// api
import { fetchUserDiaries } from "../../apis/users";
// コンポーネント
import { DiaryDialog } from "../Dialogs/DiaryDialog"

export const Calendar = () => {
  const [diary, setDiary] = useState(null)
  const [open, setOpen] = useState(false)
  // 日記Dialogを開閉する関数群
  const handleOpen = () => setOpen(true)
  const handleClose = () => setOpen(false)
  // const diaries = [{ title: "good", start: "2021-11-09" }]

  // useEffect(() => {
  //   fetchUserDiaries()
  //     .then(data => {
  //       setDiary({
  //         title: "good",
  //         start: "2021-11-10",
  //       })
  //       diaries.push(diary)
  //     })
  // }, [])

  const handleDateClick = useCallback((DateClickArg) => {
    alert(arg.dateStr);
  }, []);

  return (
    <>
      <FullCalendar
        plugins={[dayGridPlugin]}
        initialView="dayGridMonth"
        locale="ja"
        dateClick={handleDateClick}
        events={[
          { title: "event 1", start: "2021-07-01" },
          { title: "event 2", start: "2021-07-02T12:00:00" }
        ]}
      />

      <Button variant="inherit" onClick={handleOpen}>
        記録する
      </Button>
      <DiaryDialog
        open={open}
        handleClose={handleClose}
      />
      {/* <p>{diaries[0]}</p> */}
    </>
  )
}
