import React, { useState, useEffect, useReducer } from "react";
// Style
import Box from '@mui/material/Box';
import { createStyles, makeStyles } from "@material-ui/core/styles";
import Tab from '@mui/material/Tab';
import TabContext from '@mui/lab/TabContext';
import TabList from '@mui/lab/TabList';
import TabPanel from '@mui/lab/TabPanel';
import Typography from "@mui/material/Typography";
// Api
import { fetchUserBooks } from '../../apis/users';
import { fetchSearchBooks } from '../../apis/books';
// Reducer
import { bookInitialState, bookReducer } from '../../reducer/BookReducer';
// Component
import { BookList } from '../../components/Lists/BookList';
import { Search } from '../../components/Forms/Search';
import { BookSearchButton } from '../../components/Buttons/BookSearchButton';
import { BookCard } from '../../components/Lists/BookCard';
import { Loading } from '../../components/Loading';

const useStyles = makeStyles(() =>
  createStyles({
    'root': {
      alignItems: 'center',
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'center',
      maxWidth: 1000,
      marginTop: 120,
      mx: 'auto',
      width: '100%',
    },
    'search': {
      display: 'flex',
      flexDirection: 'column',
      maxWidth: 400,
      width: '100%',
    },
    'recommend': {
      display: 'flex',
      flexDirection: 'column',
      maxWidth: 600,
      width: 600,
    },
    'list': {
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'center',
      maxWidth: 1000,
      width: '100%',
    },
    'card': {
      display: 'flex',
      flexDirection: 'column',
      maxWidth: 600,
      width: 250,
      padding: 15,
    },
  }),
);

export const Mybooks = ({
  userId,
}) => {
  const classes = useStyles();
  const [keyword, setKeyword] = useState('');
  const [tab, setTab] = useState('read');
  const [bookState, bookDispatch] = useReducer(bookReducer, bookInitialState);
  const tabLabelRead = `読了 : ${bookState.readBooks.length} 冊`
  const tabLabelStack = `積読 : ${bookState.stackBooks.length} 冊`

  // ユーザーの登録本を取得する
  const myBooks = () => {
    fetchUserBooks(userId)
      .then(data => {
        bookDispatch({
          type: 'fetchSuccess',
          payload: {
            readBooks: data.read_books,
            stackBooks: data.stack_books,
          }
        });
      });
  }

  // 検索したキーワードに該当本を取得する
  const searchBooks = () => {
    bookDispatch({ type: 'posting', })
    fetchSearchBooks({ keyword: keyword })
      .then(data => {
        bookDispatch({
          type: 'postSuccess',
          payload: data.books,
        });
      });
  }

  useEffect(() => {
    myBooks();
    return () => setKeyword('');
  }, [tab, bookState.reRender])

  return (
    <Box className={classes.root}>
      <Typography variant="h3">
        <Box sx={{ letterSpacing: 10, pb: 2 }}><b>{'Testユーザー'}さんの本棚</b></Box>
      </Typography>
      <Box className={classes.search}>
        <Box item xs={12} sm={7}>
          <Box>
            <Search
              keyword={keyword}
              handleChange={e => setKeyword(e.target.value)}
            />
            <BookSearchButton
              handleSubmit={searchBooks}
            />
          </Box>
          {bookState.searchBooks.length !== 0 &&
            <>
              <h4>検索ワード : {keyword} </h4>
              <h4>検索結果 : {bookState.searchBooks.length} 件</h4>
            </>
          }
        </Box>
        <Box className={classes.recommend}>
          おすすめ本
        </Box>

      </Box>

      {bookState.fetchState !== 'ok' ? <Loading /> :

        <>
          {bookState.searchBooks.length != 0 &&
            <Box className={classes.list}>
              {bookState.searchBooks.map(book =>
                <Box
                  className={classes.card}
                  key={book.params.isbn.toString()}
                >
                  <BookCard book={book.params} />
                </Box>
              )}
            </Box>
          }

          {bookState.searchBooks.length === 0 &&
            <>
              <TabContext value={tab}>
                <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                  <TabList onChange={(event, newTab) => setTab(newTab)}>
                    <Tab label={tabLabelRead} value="read" />
                    <Tab label={tabLabelStack} value="stack" />
                  </TabList>
                </Box>
                <TabPanel value="read" className={classes.list}>
                  {bookState.readBooks.length === 0 ? (
                    <h2>読了した本はありません。</h2>
                  ) : (
                    <BookList books={bookState.readBooks} />
                  )}
                </TabPanel>
                <TabPanel value="stack" className={classes.list}>
                  {bookState.stackBooks.length === 0 ? (
                    <h2>積んでいる本はありません。</h2>
                  ) : (
                    <BookList books={bookState.stackBooks} />
                  )}
                </TabPanel>
              </TabContext>
            </>
          }
        </>
      }
    </Box>
  )
}
