import axios from "axios";
import { micropostCreate, micropostDestroy, unfollow } from "../urls";

export const postFollow = (params) => {
  return axios.post(follow, {
    followed_id: params.userId
  },
    { withCredentials: true }
  )
    .then(res => {
      if (res.data) {
        console.log('follow#create', res);
        return res.data;
      }
    })
    .catch(error => {
      console.log('follow#create', error)
      return 'nil'
    })
}

export const deleteUnfollow = (relationshipId) => {
  return axios.delete(unfollow(relationshipId), { withCredentials: true })
    .then(res => {
      console.log('unfollow#destroy', res)
      return res.data
    })
    .catch(error => {
      console.log('unfollow#destroy', error)
    })
}
