import React, { useState } from 'react';
// styles
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import TextField from '@mui/material/TextField';
import { MenuItem } from '@mui/material';

import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';
// api

export const DiaryDialog = (props) => {
  const [date, setDate] = useState(Date.now())
  const [sleepingHours, setSleepingHours] = useState(null)
  const [feeling, setFeeling] = useState(null)
  const [value, setValue] = React.useState('one');
  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  const handleSubmit = () => {
    // postDiary({
    //   date: date,
    //   sleepiHours: sleepingHours,
    //   feeling: feeling,
    // }).then(() => {
    //   setDate(Date.now())
    //   setSleepingHours(null)
    //   setFeeling(null)
    //   props.handleClose()
    // }).catch(() => {
    alert('記録失敗')
    // })
  }

  const currencies = [
    {
      value: 'USD',
      label: '$',
    },
    {
      value: 'EUR',
      label: '€',
    },
    {
      value: 'BTC',
      label: '฿',
    },
    {
      value: 'JPY',
      label: '¥',
    },
  ];

  // 新規登録ダイアログの内容を返す
  return (
    <Dialog
      open={props.open}
      onClose={props.handleClose}
    >
      <DialogTitle>
        睡眠日記を記録する
      </DialogTitle>
      <DialogContent>
        <DialogContentText>
          睡眠時間と寝起きの気分を記録しましょう。
        </DialogContentText>

        <TextField
          fullWidth variant="standard"
          label="日付"
          margin="dense"
          type="date"
          onChange={e => setDate(e.target.value)}
          value={date}
          required
        />
        <TextField
          autoFocus
          fullWidth variant="standard"
          label="睡眠時間（例：7.5)"
          margin="dense"
          type="number"
          onChange={e => setSleepingHours(e.target.value)}
          value={sleepingHours}
          required
        />
        <TextField
          fullWidth variant="standard"
          label="睡眠状態"
          margin="dense"
          type="integer"
          onChange={e => setSleepingHours(e.target.value)}
          select
          value={feeling}
          required
          SelectProps={{ native: true }}
          helperText="起きた時の気分を選んでください"
        >
          {currencies.map((option) => (
            <MenuItem key={option.value} value={option.value}>
              {option.label}
            </MenuItem>
          ))}
        </TextField>
        <Box sx={{ width: '100%' }}>
          <Tabs
            value={value}
            onChange={handleChange}
            aria-label="wrapped label tabs example"
          >
            <Tab
              value="one"
              label="アルファ"
              wrapped
            />
            <Tab value="two" label="ベータ" />
            <Tab value="three" label="シータ" />
          </Tabs>
        </Box>
      </DialogContent>

      <DialogActions>
        <Button onClick={() => { props.handleClose() }}>
          閉じる
        </Button>
        <Button onClick={handleSubmit} type='submit'>
          日記を記録する
        </Button>
      </DialogActions>
    </Dialog>
  );
}
