import React from 'react';
// Style
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import ListItem from "@mui/material/ListItem";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import Typography from "@mui/material/Typography";
// Icon
import AccountCircle from "@mui/icons-material/AccountCircle";
// Component
import { Comment } from '../Lists/Comment';

export const MicropostDialog = ({
  comments,
  handleClose,
  loginUser,
  micropost,
  open,
  user,
}) => {

  return (
    <Dialog
      open={open}
      onClose={() => handleClose()}
    >
      <DialogTitle>
        <ListItem
          key={micropost.id.toString()}
          sx={{
            display: 'flex',
            alignItems: "center",
            borderTop: 0.2,
          }}>
          <ListItemAvatar>
            <AccountCircle sx={{ fontSize: 35 }} />
          </ListItemAvatar>
          <Box sx={{ py: 3, pl: 3, flexGrow: 1 }}>
            <Typography>
              【 {user.name} さん 】 {micropost.created_at.substr(0, 19).replace('T', ' ')}
            </Typography>
            <Typography variant="h6" sx={{ pl: 1 }}>
              <Box sx={{ letterSpacing: 2, mt: 2 }}>{micropost.content}</Box>
            </Typography>
          </Box>
        </ListItem >
      </DialogTitle>
      <DialogContent>
        {comments.length !== 0 &&
          <Box px={{ ml: 2 }}>
            {
              comments.map(comment =>
                <Comment
                  comment={comment.comment}
                  loginUser={loginUser}
                  userName={comment.user.name}
                />
              )
            }
          </Box>
        }
      </DialogContent>
      <DialogActions>
        <Button onClick={() => handleClose()}>
          閉じる
        </Button>
      </DialogActions>
    </Dialog>
  );
}
